def cleanse(s):
    x = s

    def func_replwlower(m):
        return m.groups()[0].lower()

    # new 
    x = re.sub("<SOL>[0-9]*<\/SOL>", ' ', x)
    x = re.sub("\s(\w*\s){1,4}[0-3]\d[.][0-1]\d[.]20[0-3]\d[ ][0-2]\d[:][0-5]\d[:][0-5]\d[ ]\(UTC\)\s([I|S|C|D]\d{4,10})?", ' ', x)
    x = re.sub("(Dear\s\*\s)|Hello\s\*\)", ' ', x)
    x = re.sub("<[^>]*>",' ', x)     # Delete all html tags. 
    # end of new
    x = re.sub("([A-Z][\W_]+|([A-Z].)+)", func_replwlower, x)    
    x = re.sub("[\s]", ' ', x)    
    x = re.sub('•', '', x)    
    x = re.sub("[-:.,\"\(\)'><_\?\\\/\|\*]", ' ', x)    
    x = re.sub("[\s]", ' ', x)    
    x = re.sub("\s+", ' ', x)    
    return x


